import React, { useRef, useState } from "react";
import EmptyStar from "./assets/empty-star.svg";
import FilledStar from "./assets/filled-star.svg";
import "./styles.css";

const RatingSystem = () => {
  // since the rating is never rendered we can use useRef to avoid unnessary re-renders.
  const rating = useRef(0);

  // state of stars
  // using a array to map over stars and assign them an index
  const starState = useState([1, 2, 3, 4, 5]);

  const setRating = (index) => {
    // the rating is always one more than the index
    rating.current = index + 1;
    console.log(rating);
  };

  return (
    <div>
      <h1>5 star rating system</h1>
      <h2>Select a rating:</h2>
      {starState.map((star) => {
        return (
          <img
            src={EmptyStar}
            alt="empty star"
            key={index}
            onClick={() => setRating(star)}
          />
        );
      })}
    </div>
  );
};

export default function App() {
  return (
    <div className="App">
      <RatingSystem />
    </div>
  );
}
