import React, { useRef, useState } from "react";
import EmptyStar from "./assets/empty-star.svg";
import FilledStar from "./assets/filled-star.svg";
import "./styles.css";

const RatingSystem = () => {
  // since the rating is never rendered we can use useRef to avoid unnessary re-renders.
  const rating = useRef(0);

  // state of stars
  // using a array to map over stars and assign them an index
  const [starState, setStarState] = useState([
    "empty",
    "empty",
    "empty",
    "empty",
    "empty",
  ]);

  const setRating = (index) => {
    // the rating is always one more than the index
    rating.current = index + 1;

    // replace empty stars from the start start state array index of star that is clicked on
    for (let i = 0; i < rating; i++) {
      setStarState(starState.splice(0, 1, "filled"));
    }
    console.log(rating);
  };

  return (
    <div>
      <h1>5 star rating system</h1>
      <h2>Select a rating:</h2>
      {starState.map((star, index) => {
        return starState === "empty" ? (
          <img
            src={EmptyStar}
            alt="empty star"
            key={index}
            onClick={() => setRating(index)}
          />
        ) : (
          <img
            src={FilledStar}
            alt="empty star"
            key={index}
            onClick={() => setRating(index)}
          />
        );
      })}
    </div>
  );
};

export default function App() {
  return (
    <div className="App">
      <RatingSystem />
    </div>
  );
}
