import React, { useState } from "react";
import EmptyStar from "./assets/empty-star.svg";
import FilledStar from "./assets/filled-star.svg";
import "./styles.css";

const RatingSystem = () => {
  // state of stars
  // using a array to map over stars and assign them an index
  const [starState, setStarState] = useState([
    "empty",
    "empty",
    "empty",
    "empty",
    "empty",
  ]);

  const setRating = (index) => {
    // the rating is always one more than the index
    const rating = index + 1;
    const emptyStars = 5 - rating;
    const emptyArray = emptyStars * ["empty"];
    const newState = [];
    for (let i = 0; i < rating; i++) {
      // add a filled star to new array
      newState.unshift("filled");
    }

    console.log(newState);
    setStarState(newState.concat(emptyArray));
  };

  return (
    <div>
      <h1>5 star rating system</h1>
      <h2>Select a rating:</h2>
      {starState.map((star, index) => {
        console.log(star);
        return star === "empty" ? (
          <img
            src={EmptyStar}
            alt="empty star"
            key={index}
            onClick={() => setRating(index)}
          />
        ) : (
          <img
            src={FilledStar}
            alt="empty star"
            key={index}
            onClick={() => setRating(index)}
          />
        );
      })}
    </div>
  );
};

export default function App() {
  return (
    <div className="App">
      <RatingSystem />
    </div>
  );
}
